<?$pd=mysql_fetch_assoc(mysql_query("select pesan from sms_setting where id='1'"));$uu=mysql_fetch_assoc(mysql_query("select pesan from sms_setting where id='2'"));$ud=mysql_fetch_assoc(mysql_query("select pesan from sms_setting where id='3'"));if (!isset($_POST[submit2])) {echo "<form method=post><table><tr><td valign=top>Pengingat Donor Kembali</td><td valign=top>:</td><td><textarea name=pengingat_donor cols=50>$pd[pesan]</textarea></td></tr><tr><td valign=top>Ucapan Ulang Tahun</td><td valign=top>:</td><td><textarea name=ucapan_ultah cols=50>$uu[pesan]</textarea></td></tr><tr><td valign=top>Ucapan Terima Kasih</td><td valign=top>:</td><td><textarea name=ucapan_donor cols=50>$ud[pesan]</textarea></td></tr></table><input type=submit name=submit2 value='Update'>";} else {mysql_query("update sms_setting set pesan='$_POST[pengingat_donor]' where id='1'");mysql_query("update sms_setting set pesan='$_POST[ucapan_ultah]' where id='2'");mysql_query("update sms_setting set pesan='$_POST[ucapan_donor]' where id='3'");echo ("Isi Pesan telah diupdate !!    <meta HTTP-EQUIV=\"REFRESH\" CONTENT=\"1; URL=$PHP_SELF\">");}?>